import React, { useState } from 'react';
import Search from './components/Search';
import axios from 'axios';



function App() {
  const [state, setState] = useState({
    s:"",
    results: [],
    selected: {}
  });
  const apiUrl = "http://www.omdbapi.com/?i=tt3896198&apikey=4ffc7bff"

  const search = (e) => {
    if(e.key === "Enter") {
      axios(apiUrl + "&s=" + state.s)
    }
  }

  const handleInput = (e) => {
    let s = e.target.value

    setState(prevState => {
      return {
        ...prevState, s: s
      }
    });
    console.log(state.s);
  }
  return (
    <div className="App">
      <header>
        <h1>Movie Database</h1>
      </header>
      <main>
        <Search handleInput={handleInput}/>
      </main>
    </div>
  );
}

export default App;
